import React, { useState, useEffect, useCallback } from "react";
import AirportTable from "./components/AirportTable";
import AirportForm from "./components/AirportForm";
import DeleteAirportForm from "./components/DeleteAirportForm";
import AirportStatistics from "./components/AirportStatistics";
import { fetchAirports, fetchStatistics } from "./services/api";

function App() {
  const [airports, setAirports] = useState([]);
  const [filter, setFilter] = useState(""); // Filter state for searching by airport name
  const [sortBy, setSortBy] = useState("name");
  const [sortOrder, setSortOrder] = useState("asc");
  const [showForm, setShowForm] = useState(false);
  const [showDeleteForm, setShowDeleteForm] = useState(false);
  const [statistics, setStatistics] = useState({
    averageElevation: 0,
    noIataCount: 0,
    noIataAirports: [],
    topTimezones: [],
    elevationPerCountry: [],
  });

  const [pagination, setPagination] = useState({ page: 1, limit: 50, total: 0 });

  // Fetch airports and apply the filter
  // const loadAirports = useCallback(async () => {
  //   try {
  //     const { airports, total, page, limit } = await fetchAirports(
  //       sortBy,
  //       sortOrder,
  //       "", // Remove the filter here, we'll apply it locally
  //       pagination.page,
  //       pagination.limit
  //     );
  //     setAirports(airports);
  //     setPagination({ page, limit, total });
  //   } catch (error) {
  //     console.error("Error loading airports:", error);
  //   }
  // }, [sortBy, sortOrder, pagination.page, pagination.limit]);

  const loadAirports = async () => {
    try {
      const { airports } = await fetchAirports(); // ✅ only get the array
      setAirports(airports || []); // fallback to [] in case it's undefined
    } catch (error) {
      console.error('Error fetching airports:', error);
      setAirports([]); // fallback to avoid breaking the UI
    }
  };  // Fetch statistics
  const loadStatistics = useCallback(async () => {
    try {
      const stats = await fetchStatistics();
      setStatistics(stats);
    } catch (error) {
      console.error("Error loading statistics:", error);
    }
  }, []);

  // Trigger data fetch on component mount and whenever pagination, sorting, or filtering changes
  useEffect(() => {
    loadAirports();
  }, [pagination.page, pagination.limit, sortBy, sortOrder]);

  useEffect(() => {
    loadStatistics(); // Call only once when the component mounts
  }, [loadStatistics]); // Empty array ensures this is only called on mount

  // Filter the airports after data is fetched
  const filteredAirports = airports.filter((airport) =>
    airport.name.toLowerCase().includes(filter.toLowerCase())
  );

  const handleSort = (field) => {
    const newOrder = field === sortBy && sortOrder === "asc" ? "desc" : "asc";
    setSortBy(field);
    setSortOrder(newOrder);
  };

  const handleFilterChange = (e) => {
    setFilter(e.target.value);
    setPagination((prev) => ({ ...prev, page: 1 })); // reset to first page
  };

  const handleAddAirport = () => {
    setShowForm(true);
    setShowDeleteForm(false);
  };

  const handleDeleteAirport = () => {
    setShowDeleteForm(true);
    setShowForm(false);
  };

  const handleFormCancel = () => {
    setShowForm(false);
    setShowDeleteForm(false);
  };

  const handleAirportAdded = () => {
    setShowForm(false);
  };

  const handleAirportDeleted = () => {
    setShowDeleteForm(false);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <h1 className="text-3xl font-bold text-gray-900">Airport Data Management</h1>
        </div>
      </header>
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        {showForm ? (
          <AirportForm onCancel={handleFormCancel} onSuccess={handleAirportAdded} />
        ) : showDeleteForm ? (
          <DeleteAirportForm onCancel={handleFormCancel} onSuccess={handleAirportDeleted} />
        ) : (
          <div>
            <div className="flex justify-between items-center mb-6">
              <input
                type="text"
                placeholder="Filter by airport name..."
                className="px-4 py-2 border rounded-md w-64"
                value={filter}
                onChange={handleFilterChange}
              />
              <div className="flex gap-2">
                <button
                  onClick={handleAddAirport}
                  className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
                >
                  Add New Airport
                </button>
                <button
                  onClick={handleDeleteAirport}
                  className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700"
                >
                  Delete Airport
                </button>
              </div>
            </div>

            <AirportStatistics statistics={statistics} />

            <AirportTable
              airports={filteredAirports} // Pass the filtered airports here
              searchQuery={filter} // Pass the filter (searchQuery) here
              sortBy={sortBy}
              sortOrder={sortOrder}
              onSort={handleSort}
              pagination={pagination}
              setPagination={setPagination}
            />
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
