import { useEffect, useState } from "react";

const AirportTable = ({ airports, searchQuery, sortBy, sortOrder, onSort, pagination, setPagination }) => {
  const [filteredAirports, setFilteredAirports] = useState(airports);

  // Apply filter based on airport name search query
  useEffect(() => {
    if (searchQuery) {
      const filtered = airports.filter((airport) =>
        airport.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setFilteredAirports(filtered);
    } else {
      setFilteredAirports(airports);
    }
  }, [searchQuery, airports]);

  const totalPages = Math.ceil(pagination.total / pagination.limit);

  return (
    <div>
      {/* Table code */}
      <table className="w-full border">
        <thead>
          <tr>
            <th className="p-2 border" onClick={() => onSort("name")}>Name</th>
            {/* other columns */}
          </tr>
        </thead>
        <tbody>
          {filteredAirports.map((airport, idx) => (
            <tr key={idx} className={airport.elevation > 8000 ? "bg-red-100 font-bold" : ""}>
              <td className="p-2 border">{airport.name}</td>
              <td className="p-2 border">{airport.city}</td>
              <td className="p-2 border">{airport.country}</td>
              {/* other columns */}
            </tr>
          ))}
        </tbody>
      </table>

      {/* Pagination Controls */}
      <div className="flex justify-center gap-2 mt-4">
        <button
          disabled={pagination.page === 1}
          onClick={() => setPagination((prev) => ({ ...prev, page: pagination.page - 1 }))}
          className="px-3 py-1 bg-gray-200 hover:bg-gray-300 rounded disabled:opacity-50"
        >
          Previous
        </button>
        <span className="px-3 py-1 text-sm text-gray-700">
          Page {pagination.page} of {totalPages}
        </span>
        <button
          disabled={pagination.page === totalPages}
          onClick={() => setPagination((prev) => ({ ...prev, page: pagination.page + 1 }))}
          className="px-3 py-1 bg-gray-200 hover:bg-gray-300 rounded disabled:opacity-50"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default AirportTable;
