import { useState, useEffect } from 'react';
import { fetchAirports, addAirport } from '../services/api';  // Make sure you have the addAirport API function
import AirportForm from './AirportForm';  // Import the form component

const App = () => {
  const [airports, setAirports] = useState([]);
  const [showForm, setShowForm] = useState(false);

  // Fetch airports from the API
  const loadAirports = async () => {
    try {
      const data = await fetchAirports(); // Adjust based on your API
      setAirports(data);
    } catch (error) {
      console.error('Error fetching airports:', error);
    }
  };

  useEffect(() => {
    loadAirports();  // Fetch airports on component mount
  }, []);  // Empty dependency array to run only on mount

  // Handle the success of adding an airport
  const handleSuccess = (newAirport) => {
    setAirports(prevAirports => [...prevAirports, newAirport]); // Add the new airport directly to the state
    setShowForm(false);  // Close the form
  };

  const handleCancel = () => {
    setShowForm(false);  // Close the form without adding an airport
  };

  return (
    <div>
      <h1>Airports</h1>
      <button onClick={() => setShowForm(true)}>Add New Airport</button>

      {showForm && (
        <AirportForm onCancel={handleCancel} onSuccess={handleSuccess} />
      )}

      <div>
        <h2>Airport List</h2>
        <ul>
          {airports.map((airport) => (
            <li key={airport.id}>{airport.name}</li> // Adjust based on your data structure
          ))}
        </ul>
      </div>
    </div>
  );
};

export default App;
